--
-- Cấu trúc bảng cho bảng `dmtintuc`
--

CREATE TABLE `dmtintuc` (
  `id_DMTT` int(11) NOT NULL,
  `nameDMTT` varchar(250) NOT NULL,
  `banner1` varchar(255) DEFAULT NULL,
  `banner2` varchar(255) DEFAULT NULL,
  `gioiThieu` mediumtext NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Đang đổ dữ liệu cho bảng `dmtintuc`
--

INSERT INTO `dmtintuc` (`id_DMTT`, `nameDMTT`, `banner1`, `banner2`, `gioiThieu`) VALUES
(2, 'Khám phá', 'saleup.jpg', 'khampha.JPG', '<p>T&igrave;nh y&ecirc;u kh&aacute;m ph&aacute; thế giới của bạn đ&atilde; gi&uacute;p duy tr&igrave; ng&agrave;nh du lịch trong những thời điểm hỗn loạn n&agrave;y, v&agrave; khi thế giới bắt đầu mở cửa trở lại, bạn l&agrave; một trong những người đầu ti&ecirc;n quay trở lại đ&oacute;. Những hiểu biết của bạn về những chuyến đi đ&oacute; - v&agrave; tr&ecirc;n c&aacute;c chuyến đi trước đại dịch của bạn - đ&atilde; th&ocirc;ng b&aacute;o cho c&aacute;c bảng xếp hạng n&agrave;y về c&aacute;c kh&aacute;ch sạn, h&ograve;n đảo, nh&agrave; điều h&agrave;nh tour du lịch</p>\r\n'),
(3, 'Cảm hứng chuyến đi', NULL, 'camhung.JPG', '<p>T&igrave;m kiếm nơi nghỉ ngơi&nbsp; ho&agrave;n hảo? Cho d&ugrave; bạn muốn đến b&atilde;i biển trong một ng&agrave;y cuối tuần d&agrave;i hay kh&aacute;m ph&aacute; một th&agrave;nh phố mới, <em>Travel Package&nbsp;</em>&nbsp;c&oacute; lời khuy&ecirc;n du lịch, tin tức v&agrave; th&ocirc;ng tin về c&aacute;c điểm đến th&uacute; vị nhất cho những nơi nghỉ ngơi cuối tuần tốt nhất.</p>\r\n'),
(4, 'Lên kế hoạch', NULL, 'kehoach.JPG', '<p>Thế giới của c&aacute;c kh&aacute;ch sạn, khu nghỉ dưỡng, thi&ecirc;n nhi&ecirc;n cũng rộng v&agrave; đa dạng như thế giới ch&uacute;ng ta đang sống. Rốt cuộc, đối với mỗi điểm đến, thường c&oacute; một v&agrave;i (nếu kh&ocirc;ng phải h&agrave;ng chục, hoặc thậm ch&iacute; h&agrave;ng trăm) lựa chọn để ở. Cho d&ugrave; bạn đang t&igrave;m kiếm một kh&aacute;ch sạn lớn sang trọng v&agrave; mang t&iacute;nh biểu tượng, một cửa h&agrave;ng kỳ quặc v&agrave; c&oacute; đầu &oacute;c m&aacute;t mẻ, một nh&agrave; trọ đẹp như tranh vẽ ngay từ một cuốn s&aacute;ch truyện, một khu nghỉ m&aacute;t b&ecirc;n bờ biển bao gồm tất cả, hoặc một k&yacute; t&uacute;c x&aacute; độc đ&aacute;o hoặc trải nghiệm.</p>\r\n'),
(5, 'Ẩm thực', 'banner4.jpg', 'monan.JPG', '<p>Những m&oacute;n ăn , thực đơn ẩm thực được nấu nướng mang phong c&aacute;ch bản sắc văn hoa của từng địa điểm du lịch chắc chắn sẽ kh&ocirc;ng l&agrave;m bạn thất vọng!</p>\r\n'),
(6, 'Chuyến đi', 'banner5.jpg', 'chuyendi.JPG', '<p>Từ những ngọn n&uacute;i trượt tuyết tốt nhất m&ugrave;a đ&ocirc;ng v&agrave; những chuyến đi tr&ecirc;n những con đường t&aacute;n l&aacute; tr&ecirc;n c&ugrave;ng của m&ugrave;a thu đến c&aacute;c điểm đến đầy hoa của m&ugrave;a xu&acirc;n v&agrave; những b&atilde;i biển đẹp nhất m&ugrave;a h&egrave;, ch&uacute;ng t&ocirc;i đang chia sẻ những điểm đến mơ mộng nhất mỗi m&ugrave;a.</p>\r\n'),
(7, 'Book Trip', 'maybay.jpg', 'bannerdmtour.JPG', '<p>H&atilde;y đăng k&iacute; ngay h&ocirc;m nay để c&oacute; trải nghiệm tốt c&ugrave;ng gia đ&igrave;nh v&agrave; bạn b&egrave; th&acirc;n y&ecirc;u của bạn. Khảo s&aacute;t chọn lọc dịch vụ chất lượng cao , đảo bảo an to&agrave;n tuyệt đối!!</p>\r\n');

